mapper.update:
	UPDATE feedcontent
   		SET feedContent = #{feedContent}

   			,feedContent_photo = #{feedContent_photo}


  			 WHERE feedSeq = #{feedSeq}

	UPDATE feedcontent
   		SET feedContent_photo = null
  			 WHERE feedSeq = #{feedSeq}

mapper.delete:
		DELETE 
 			FROM feedcomment
 				WHERE feedSeq = #{feedSeq} AND commentSeq = #{commentSeq}
